// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Calculator {
  uint256 result = 0;

  function add(uint256 num) external  {
    result += num;
  }

  function subtract(uint256 num) external   {
    result -= num;
  }

  function multiply(uint256 num) external  {
    result *= num;
  }

  function get() public view returns (uint256) {
    return result;
  }
}

